export class StringHelper {
  /**
   * Warning: generated by ChatGPT4
   * @param input
   * @param algorithm
   * @returns
   */
  static generateHash(
    input: string,
    algorithm: 'MD5' | 'SHA_1' | 'SHA_256'
  ): string {
    const algorithms = {
      MD5: Utilities.DigestAlgorithm.MD5,
      SHA_1: Utilities.DigestAlgorithm.SHA_1,
      SHA_256: Utilities.DigestAlgorithm.SHA_256,
    };

    if (!algorithms[algorithm]) {
      throw new Error(`Not supported algorithm: ${algorithm}`);
    }

    const rawHash = Utilities.computeDigest(algorithms[algorithm], input);
    return rawHash
      .map((byte) => ('0' + (byte & 0xff).toString(16)).slice(-2)) // 16進数に変換
      .join('');
  }
}
